export default {
	mapping: [
		"Sunday",
		"Monday",
		"Tuesday",
		"Wednesday",
		"Thursday",
		"Friday",
		"Saturday"
	],
	updateDate: async() => {
		overview_per_day.getDayString();
		await orders_per_day.run();
		await average_revenue_last_month.run();
		await orders_per_day_per_category.run();
	},
	getDayString: () => {
		//write code here
		const date_var = new Date(select_date.selectedOptionValue ? select_date.selectedOptionValue : select_date.options[0]);
		const day_number = date_var.getDay();
		const day_string = this.mapping[day_number];
		storeValue("dayString", day_string)
	},
	onPageLoad: async() => {
		await dates_open.run();
		await orders_per_day.run();
		await average_revenue_last_month.run();
		overview_per_day.getDayString()
	}
}
